mapper.select:
SELECT re.id,re.name,re.parentId,re.resUrl FROM resources re LEFT JOIN role_resources rr ON re.id = rr.resourcesId LEFT JOIN user_role ur ON rr.roleId =ur.roleId WHERE ur.userId=#{userid}
AND re.type= #{type}
GROUP BY re.id ORDER BY re.sort ASC

SELECT re.id,re.name,re.parentId,re.resUrl FROM resources re LEFT JOIN role_resources rr ON re.id = rr.resourcesId LEFT JOIN manager_role mr ON rr.roleId =mr.roleId WHERE mr.managerId=#{managerid}
AND re.type= #{type}
GROUP BY re.id ORDER BY re.sort ASC
